import React, { useEffect, useState } from 'react';
import axios from 'axios';

interface Video {
    id: { videoId: string };
    snippet: { title: string; thumbnails: { default: { url: string } } };
}

const apiKey = "AIzaSyAkrB5MUD957UvAfLvrFQNEhgEByGSjRw8";
// URL initial pour récupérer les 10 dernières vidéos de la chaîne spécifiée
const channelId = "UCWeg2Pkate69NFdBeuRFTAw";
const initialApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&channelId=${channelId}&part=snippet,id&order=date&maxResults=10`;

const VideoList: React.FC = () => {
    const [videos, setVideos] = useState<Video[]>([]);
    const [searchTerm, setSearchTerm] = useState('');

    // Chargement initial des vidéos de la chaîne spécifiée
    useEffect(() => {
        axios.get(initialApiUrl).then((response) => {
            setVideos(response.data.items);
        });
    }, []);

    // Fonction de recherche
    const handleSearch = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        // URL pour rechercher des vidéos sur YouTube avec le terme de recherche
        const searchApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&q=${searchTerm}&part=snippet,id&maxResults=10`;
        axios.get(searchApiUrl).then((response) => {
            // Ajouter les vidéos trouvées à la liste existante
            setVideos(prevVideos => [...prevVideos, ...response.data.items]);
        });
    };

    return (
        <div>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Rechercher des vidéos"
                />
                <button type="submit">Rechercher</button>
            </form>
            {videos.map((video) => (
                <div key={video.id.videoId}>
                    <h2>{video.snippet.title}</h2>
                    <iframe
                        width="600"
                        style={{ aspectRatio: "16/9" }}
                        src={`https://www.youtube.com/embed/${video.id.videoId}`}
                        frameBorder="0"
                        allowFullScreen
                    ></iframe>
                </div>
            ))}
        </div>
    );
};

export default VideoList;
