import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../App.css';

interface Video {
    id: { videoId: string };
    snippet: { title: string; thumbnails: { default: { url: string } } };
}

const apiKey = "AIzaSyDTYkIi2vjdW6IRmXGMhr4df3H5RP35TjI";
const channelId = "UCWeg2Pkate69NFdBeuRFTAw";

const VideoList: React.FC = () => {
    const [videos, setVideos] = useState<Video[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState<Video[]>([]);
    const [currentVideoId, setCurrentVideoId] = useState<string | null>(null);

    useEffect(() => {
        fetchInitialVideos();
    }, []);

    const fetchInitialVideos = () => {
        const initialApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&channelId=${channelId}&part=snippet,id&order=date&maxResults=10`;
        axios.get(initialApiUrl).then(response => {
            setVideos(response.data.items);
        });
    };

    const handleSearch = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        const searchApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&q=${searchTerm}&part=snippet,id&maxResults=10`;
        axios.get(searchApiUrl).then(response => {
            setSearchResults(response.data.items);
        });
    };

    const handleAddVideo = (video: Video) => {
        setVideos(prevVideos => [...prevVideos, video]);
        setSearchResults([]);
        setSearchTerm('');
    };

    const handleRemoveVideo = (videoId: string) => {
        setVideos(prevVideos => prevVideos.filter(video => video.id.videoId !== videoId));
    };

    const handleVideoClick = (videoId: string) => {
        setCurrentVideoId(videoId);
    };

    const renderVideoList = (videoList: Video[]) => {
        return (
            <div className="added-videos">
                {videoList.map(video => (
                    <div key={video.id.videoId} className="added-video" onClick={() => handleVideoClick(video.id.videoId)}>
                        <img src={video.snippet.thumbnails.default.url} alt={video.snippet.title} />
                        <p>{video.snippet.title}</p>
                    </div>
                ))}
            </div>
        );
    };

    return (
        <div className="App">
            <div className="App-header">
                <h1>Dernières vidéos de la chaîne YouTube</h1>
            </div>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Rechercher des vidéos"
                />
                <button type="submit">Rechercher</button>
            </form>
            <div className="video-list-container">
                {renderVideoList(videos)}
            </div>
            <div className="video-player">
                {currentVideoId && (
                    <iframe
                        width="100%"
                        height="100%"
                        src={`https://www.youtube.com/embed/${currentVideoId}`}
                        frameBorder="0"
                        allowFullScreen
                    />
                )}
            </div>
        </div>
    );
};

export default VideoList;
