import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../App.css'; // Assurez-vous que le chemin vers votre fichier CSS est correct

interface Video {
    id: string;
    title: string;
}

interface UserLibrary {
    name: string;
    videos: Video[];
}

const apiKey = "AIzaSyDNZclk4gyX-z3Ii_lEj7FbqWhKiXx6-VY";
const channelId = "UCWeg2Pkate69NFdBeuRFTAw";
const initialApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&channelId=${channelId}&part=snippet,id&order=date&maxResults=2`;

const VideoList: React.FC = () => {
    const { username } = useParams<{ username: string }>();
    const [videos, setVideos] = useState<Video[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState<Video[]>([]);
    const [selectedVideo, setSelectedVideo] = useState<string | null>(null);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        if (username) {
            axios.get<UserLibrary>(`/${username}.lib`)
                .then((response) => {
                    setVideos(response.data.videos);
                })
                .catch((error) => {
                    setError(error.message);
                });
        }
    }, [username]);

    const handleSearch = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        const searchApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&q=${searchTerm}&part=snippet,id&maxResults=10`;
        axios.get(searchApiUrl)
            .then((response) => {
                const results = response.data.items.map((item: any) => ({
                    id: item.id.videoId,
                    title: item.snippet.title,
                }));
                setSearchResults(results);
            })
            .catch((error) => {
                setError(error.message);
            });
    };

    const handleAddVideo = (video: Video) => {
        setVideos(prevVideos => [...prevVideos, video]);
        setSearchResults([]);
        setSearchTerm('');
    };

    const handleRemoveVideo = (videoId: string) => {
        const updatedVideos = videos.filter(video => video.id !== videoId);
        setVideos(updatedVideos);
    };

    const handleVideoClick = (videoId: string) => {
        setSelectedVideo(videoId);
    };

    return (
        <div className="app-container">
            <div className="video-list-container">
                {error && <p>Erreur: {error}</p>}
                <form onSubmit={handleSearch}>
                    <input
                        type="text"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        placeholder="Rechercher des vidéos"
                    />
                    <button type="submit">Rechercher</button>
                </form>
                <div className="video-list">
                    {searchResults.map((video) => (
                        <div key={video.id} className="video-container" onClick={() => handleAddVideo(video)}>
                            <img src={`https://img.youtube.com/vi/${video.id}/default.jpg`} alt={video.title} />
                            <h3>{video.title}</h3>
                        </div>
                    ))}
                </div>
                <h2>Vidéos ajoutées</h2>
                <div className="added-videos">
                    {videos.map((video) => (
                        <div key={video.id} className="added-video">
                            <img
                                src={`https://img.youtube.com/vi/${video.id}/default.jpg`}
                                alt={video.title}
                                onClick={() => handleVideoClick(video.id)}
                            />
                            <h3>{video.title}</h3>
                            <button onClick={() => handleRemoveVideo(video.id)}>Supprimer</button>
                        </div>
                    ))}
                </div>
            </div>
            <div className="video-player">
                {selectedVideo && (
                    <iframe
                        width="100%"
                        height="100%"
                        src={`https://www.youtube.com/embed/${selectedVideo}`}
                        frameBorder="0"
                        allowFullScreen
                        title="YouTube Video Player"
                    ></iframe>
                )}
            </div>
        </div>
    );
};

export default VideoList;
