import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css'; // Assurez-vous que le chemin vers votre fichier CSS est correct

interface Video {
    id: { videoId: string };
    snippet: { title: string; thumbnails: { default: { url: string } } };
}

const apiKey = "AIzaSyDTYkIi2vjdW6IRmXGMhr4df3H5RP35TjI";
const channelId = "UCWeg2Pkate69NFdBeuRFTAw";
const initialApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&channelId=${channelId}&part=snippet,id&order=date&maxResults=10`;

const VideoList: React.FC = () => {
    const [videos, setVideos] = useState<Video[]>([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState<Video[]>([]);

    useEffect(() => {
        axios.get(initialApiUrl).then((response) => {
            setVideos(response.data.items);
        });
    }, []);

    const handleSearch = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        const searchApiUrl = `https://www.googleapis.com/youtube/v3/search?key=${apiKey}&q=${searchTerm}&part=snippet,id&maxResults=10`;
        axios.get(searchApiUrl).then((response) => {
            setSearchResults(response.data.items);
        });
    };

    const handleAddVideo = (video: Video) => {
        setVideos(prevVideos => [...prevVideos, video]);
        setSearchResults([]);
        setSearchTerm('');
    };

    return (
        <div className="App">
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Rechercher des vidéos"
                />
                <button type="submit">Rechercher</button>
            </form>
            <div className="video-list">
                {searchResults.map((video) => (
                    <div key={video.id.videoId} className="video-container" onClick={() => handleAddVideo(video)}>
                        <img src={video.snippet.thumbnails.default.url} alt={video.snippet.title} />
                        <h3>{video.snippet.title}</h3>
                    </div>
                ))}
            </div>
            <h2>Vidéos ajoutées</h2>
            <div className="video-list">
                {videos.map((video) => (
                    <div key={video.id.videoId} className="video-container">
                        <h3>{video.snippet.title}</h3>
                        <iframe
                            width="100%"
                            style={{ aspectRatio: "16/9" }}
                            src={`https://www.youtube.com/embed/${video.id.videoId}`}
                            frameBorder="0"
                            allowFullScreen
                        ></iframe>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default VideoList;
